import React from "react";
import Booksearch from "../../components/BookSearch/Booksearch";
import Booksaved from "../../components/BookSaved/Booksaved";

export default class Bookpagecontroller extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewSaved: false
        };
        this.handlePageToggle = this.handlePageToggle.bind(this);
    };

    handlePageToggle = () => {
        console.log(this.state.viewSaved);
        this.setState(prevState => ({
            viewSaved: !prevState.viewSaved
        }));
    };

    Viewsaved = (props) => {
        return (
          <button onClick={props.handlePageToggle}>
            View Saved Books
          </button>
        );
      }
      
      Viewsearch = (props) => {
        return (
          <button onClick={props.handlePageToggle}>
            View Search Books
          </button>
        );
      }

      renderButtonText = (props) => {
        const text = this.state.viewSaved ? "Saved" : "Search";
        return text; 
      }

    render() {
        return (
            <div>
            <button type="button" onClick={this.handlePageToggle} class="btn btn-primary btn-lrg m-3">{this.renderButtonText}</button>               
             {this.state.viewSaved ? (
                    <div>
                        <Booksaved />
                    </div>
                ) : (
                        <div>
                            <Booksearch />
                        </div>
                    )}
            </div>
        );
    }
}
