import React from "react";
import Booksearch from "../../components/BookSearch/Booksearch";
import Booksaved from "../../components/BookSaved/Booksaved";
import Bookseesearch from "./Bookseesearch";
import Bookseesaved from "./Bookseesaved";

export default class Bookpagecontroller extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewSaved: false
        };
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    };

    handleShowSaved() {
        this.setState({ viewSaved: true });
    }

    handleShowSearch() {
        this.setState({ viewSaved: false });
    }

    render() {
        const viewSaved = this.state.viewSaved;
        let button;
        if (viewSaved) {
            button = <Viewsaved onClick={this.handleShowSearch} />; 
        }
        if (this.state.viewSaved === true) {
            return (

                <Bookseesaved />
            );
        } else {
            return (

                <Booksearch />
            );
        }
    }
}
