import React from "react";

export default class Booktoggler extends React.Component {
    constructor(props) {
      super(props);
      this.handleLoginClick = this.handleLoginClick.bind(this);
      this.handleLogoutClick = this.handleLogoutClick.bind(this);
      this.state = {isLoggedIn: false};
    }
  
    handleLoginClick() {
      this.setState({isLoggedIn: true});
    }
  
    handleLogoutClick() {
      this.setState({isLoggedIn: false});
    }
  
    render() {
      const isLoggedIn = this.state.isLoggedIn;
      let button;
      
  
      if (isLoggedIn) {
        button = <Viewsearch onClick={this.handleViewSearchClick} />;
      } else {
        button = <Viewsaved onClick={this.handleViewSavedClick} />;
      }
  
      return (
        <div>
          <Booktoggler isLoggedIn={isLoggedIn} />
          {button}
        </div>
      );
    }
  }
