import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import "./SaveBtn.css";

function SaveBtn(props) {
  const title = props.volumeinfo.title;
  const authors = props.volumeinfo.authors;
  const link = props.volumeinfo.previewLink;
  return (
    <span className="save-btn" role="button" tabIndex="0">
      <button className="saveLink" onClick={() => { props.saveBook(title, authors, link) }}><FontAwesomeIcon icon={faSave} /> </button>
    </span>
  );
}

export default SaveBtn;
