import React from "react";
import Booksearch from "../../components/BookSearch/Booksearch";
import Booksaved from "../../components/BookSaved/Booksaved";
import Bookseesearch from "./Bookseesearch";
import Bookseesaved from "./Bookseesaved";

export default class Bookpagecontroller extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewSaved: false
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    };

    handleInputChange = e => {
        const name = e.target.name;
        const value = e.target.value;
        this.setState({
            [name]: value
        });
    };

    handleFormSubmit = (e, prevState) => {
        e.preventDefault();
        console.log(prevState);
        console.log(this.state.viewSaved);
        const newState = !prevState
        this.setState({ "viewSaved": newState });
    };

    render() {
        return (
            <div>
                {this.state.viewSaved ? (
                    <div>
                    <Bookseesearch />
                    <Booksaved />
                    </div>
            ) : (
                <div>
                        <Bookseesaved />
                        <Booksearch />
                        </div>
            )}
            </div>
        );
    }
}
